mapper.select:
SELECT CASE WHEN COUNT(*) = 0 THEN 'false'
ELSE 'true'
end
FROM
HR_OFFAPPLY_MST_X10005
WHERE COMPANY_CD = #{P_COMPANY_CD}
AND EMP_NO =
#{P_EMP_NO}

AND START_DT <= #{P_END_DT}
AND END_DT >= #{P_START_DT}

AND ATHZ_ST_CD != '4'

mapper.insert:
INSERT INTO HR_OFFAPPLY_MST_X10005 (COMPANY_CD, REQ_NO,
DNLGB_CD, BIZAREA_CD, DEPT_CD, EMP_NO, PSTN_CD, OGRP_CD, POSI_CD, DNL_CD, REQ_EMP_NO, START_DT, END_DT, REQ_DY, REQ_DT, REASON_DC, ATHZ_ST_CD, INSERT_ID, INSERT_IP, INSERT_DTS
) VALUES (#{P_COMPANY_CD}, #{P_REQ_NO},
#{P_DNLGB_CD}, #{P_BIZAREA_CD}, #{P_DEPT_CD}, #{P_EMP_NO}, #{P_PSTN_CD}, #{P_OGRP_CD}, #{P_POSI_CD}, #{P_DNL_CD}, #{P_REQ_EMP_NO}, #{P_START_DT}, #{P_END_DT}, #{P_REQ_DY}, #{P_REQ_DT}, #{P_REASON_DC}, 1, #{P_INSERT_ID}, #{P_INSERT_IP}, #{P_INSERT_DTS}
)

mapper.update:
UPDATE HR_OFFAPPLY_MST_X10005
BIZAREA_CD = #{P_BIZAREA_CD}, DEPT_CD = #{P_DEPT_CD}, EMP_NO = #{P_EMP_NO}, PSTN_CD = #{P_PSTN_CD}, OGRP_CD = #{P_OGRP_CD}, POSI_CD = #{P_POSI_CD}, DNL_CD = #{P_DNL_CD}, REQ_EMP_NO = #{P_REQ_EMP_NO}, START_DT = #{P_START_DT}, END_DT = #{P_END_DT}, REQ_DY = #{P_REQ_DY}, REASON_DC = #{P_REASON_DC}, UPDATE_ID = #{P_UPDATE_ID}, UPDATE_IP = #{P_UPDATE_IP}, UPDATE_DTS = #{P_UPDATE_DTS}
WHERE COMPANY_CD = #{P_COMPANY_CD} AND REQ_NO = #{P_REQ_NO}

UPDATE HR_OFFAPPLY_MST_X10005

	ATHZ_ST_CD = #{P_ATHZ_ST_CD},
	UPDATE_ID = #{P_UPDATE_ID},
	UPDATE_IP = #{P_UPDATE_IP},
	UPDATE_DTS = #{P_UPDATE_DTS}

WHERE
COMPANY_CD = #{P_COMPANY_CD}
AND REQ_NO = #{P_REQ_NO}

mapper.delete:
DELETE FROM HR_OFFAPPLY_MST_X10005
WHERE COMPANY_CD =
#{P_COMPANY_CD}
AND REQ_NO = #{P_REQ_NO}
