mapper.select:
SELECT USE_YN , JOB_FG , ACLF_NO , VER_CD , LCLAS_CD , LCLAS_NM , MLSFC_CD , MLSFC_NM , S_CSF_CD , S_CSF_NM , TCLF_CD , TCLF_NM , ACLF_CD , ACLF_NM , ACLF_LV , ACLF_TXT FROM HR_JOBDIVBASE_MST hjm
AND HJM.VER_CD = ( SELECT MAX(HJMD.VER_CD) FROM HR_JOBDIVBASE_MST HJMD WHERE HJMD.COMPANY_CD = #{COMPANY_CD} AND HJMD.ACLF_NO = HJM.ACLF_NO )
AND COMPANY_CD = #{COMPANY_CD}
AND USE_YN = #{USE_YN}
AND JOB_FG = #{JOB_FG}
ORDER BY VER_CD


SELECT DISTINCT ACLF_NO, VER_CD , ACLF_NO , VER_CD , AELM_CD , AELM_NM , AELM_LV
FROM HR_JOBDIVBASE_DTL hjd 
WHERE ACLF_NO = #{ACLF_NO}
AND VER_CD = #{VER_CD}


SELECT DISTINCT HJD.AELM_CD
			  , HJD.ACLF_NO
			  , HJD.VER_CD 
			  , HJD.PCFM_CD 
			  , HJD.PCFM_DC 
FROM HR_JOBDIVBASE_DTL hjd
WHERE ACLF_NO = #{ACLF_NO}
AND VER_CD = #{VER_CD}
AND COMPANY_CD = #{COMPANY_CD}


SELECT ACLF_NO,				
	   VER_CD,				
	   AELM_CD,				
	   PCFM_CD,				
	   NEED_RQISIT_FG,		
	   NEED_RQISIT_CD, 		
	   NEED_RQISIT_NM 		
FROM HR_JOBDIVBASE_SDTL
WHERE COMPANY_CD = #{COMPANY_CD}
	AND ACLF_NO = #{ACLF_NO}
	AND VER_CD = #{VER_CD}
	AND AELM_CD = #{AELM_CD}
	AND PCFM_CD = #{PCFM_CD}
ORDER BY NEED_RQISIT_CD, NEED_RQISIT_FG
