mapper.select:

SELECT DISTINCT ACLF_NO, VER_CD , ACLF_NO , VER_CD , AELM_CD , AELM_NM , AELM_LV
FROM HR_JOBDIVBASE_DTL hjd 
WHERE ACLF_NO = #{ACLF_NO}
AND VER_CD = #{VER_CD}


SELECT DISTINCT HJD.AELM_CD
			  , HJD.ACLF_NO
			  , HJD.VER_CD 
			  , HJD.PCFM_CD 
			  , HJD.PCFM_DC 
FROM HR_JOBDIVBASE_DTL hjd
WHERE ACLF_NO = #{ACLF_NO}
AND VER_CD = #{VER_CD}
AND AELM_CD = #{AELM_CD}
AND COMPANY_CD = #{COMPANY_CD}
ORDER BY AELM_CD


SELECT ACLF_NO,				
	   VER_CD,				
	   AELM_CD,				
	   PCFM_CD,				
	   NEED_RQISIT_FG,		
	   NEED_RQISIT_CD, 		
	   NEED_RQISIT_NM 		
FROM HR_JOBDIVBASE_SDTL
WHERE COMPANY_CD = #{COMPANY_CD}
	AND ACLF_NO = #{ACLF_NO}
	AND VER_CD = #{VER_CD}
	AND AELM_CD = #{AELM_CD}
	AND PCFM_CD = #{PCFM_CD}
ORDER BY NEED_RQISIT_CD, NEED_RQISIT_FG
