mapper.select:

SELECT DISTINCT ACLF_NO, VER_CD , ACLF_NO , VER_CD , AELM_CD , AELM_NM , AELM_LV
FROM HR_JOBDIVBASE_DTL hjd 
WHERE ACLF_NO = #{ACLF_NO}
AND VER_CD = #{VER_CD}
ORDER BY AELM_CD


SELECT DISTINCT HJD.AELM_CD
			  , HJD.AELM_NM
			  , HJD.ACLF_NO
			  , HJD.VER_CD 
			  , HJD.PCFM_CD 
			  , HJD.PCFM_DC 
FROM HR_JOBDIVBASE_DTL hjd
WHERE ACLF_NO = #{ACLF_NO}
AND VER_CD = #{VER_CD}
AND AELM_CD = #{AELM_CD}
AND COMPANY_CD = #{COMPANY_CD}
ORDER BY AELM_CD

mapper.insert:
INSERT INTO HR_JOBDIVBASE_DTL( COMPANY_CD, AELM_CD, ACLF_NO, VER_CD, AELM_NM, AELM_LV, PCFM_CD ) VALUES ( #{company_cd}, #{aelm_cd}, #{aclf_no}, ( SELECT MAX(VER_CD) FROM HR_JOBDIVBASE_MST hjm WHERE ACLF_NO = #{aclf_no} ), #{AELM_NM}, #{AELM_LV},
#{PCFM_CD}
' '
)
