mapper.select:
SELECT USE_YN , JOB_FG , ACLF_NO , VER_CD , LCLAS_CD , LCLAS_NM , MLSFC_CD , MLSFC_NM , S_CSF_CD , S_CSF_NM , TCLF_CD , TCLF_NM , ACLF_CD , ACLF_NM , ACLF_LV , ACLF_TXT FROM HR_JOBDIVBASE_MST hjm
AND HJM.VER_CD = ( SELECT MAX(HJMD.VER_CD) FROM HR_JOBDIVBASE_MST HJMD WHERE HJMD.COMPANY_CD = #{COMPANY_CD} AND HJMD.ACLF_NO = HJM.ACLF_NO )
AND COMPANY_CD = #{COMPANY_CD}
AND USE_YN = #{USE_YN}
AND JOB_FG = #{JOB_FG}
ORDER BY VER_CD

mapper.delete:

	DELETE
	FROM HR_JOBDIVBASE_MST
	WHERE ACLF_NO = #{aclf_no}
    AND JOB_FG = #{job_fg}
    AND VER_CD = #{ver_cd}
