mapper.select:

SELECT DISTINCT ACLF_NO, VER_CD , ACLF_NO , VER_CD , AELM_CD , AELM_NM , AELM_LV , AELM_CD AS OG_AELM_CD
FROM HR_JOBDIVBASE_DTL hjd 
WHERE ACLF_NO = #{ACLF_NO}
AND VER_CD = #{VER_CD}
ORDER BY AELM_CD

mapper.update:

UPDATE	HR_JOBDIVBASE_DTL
SET		AELM_CD = #{AELM_CD},
		AELM_NM = #{AELM_NM},
		AELM_LV = #{AELM_LV},
		UPDATE_DTS = SYSDATE,
		UPDATE_IP = #{IP},
		UPDATE_ID = #{USER_ID}
WHERE	ACLF_NO = #{ACLF_NO}
AND		VER_CD = #{VER_CD}
AND		COMPANY_CD = #{COMPANY_CD}
AND		AELM_CD = #{OG_AELM_CD}

mapper.delete:
DELETE
FROM HR_JOBDIVBASE_DTL
WHERE	ACLF_NO = #{ACLF_NO}
AND		VER_CD = #{VER_CD}
AND		COMPANY_CD = #{COMPANY_CD}
AND		AELM_CD = #{AELM_CD}

mapper.update:

UPDATE	HR_JOBDIVBASE_DTL
SET		PCFM_CD = #{PCFM_CD},
		PCFM_DC = #{PCFM_DC},
		UPDATE_DTS = SYSDATE,
		UPDATE_IP = #{IP},
		UPDATE_ID = #{USER_ID}
WHERE	ACLF_NO = #{ACLF_NO}
AND		VER_CD = #{VER_CD}
AND		AELM_CD = #{AELM_CD}
AND		PCFM_CD = #{OG_PCFM_CD}
AND		COMPANY_CD = #{COMPANY_CD}

mapper.delete:
DELETE
FROM HR_JOBDIVBASE_DTL
WHERE	ACLF_NO = #{ACLF_NO}
AND		VER_CD = #{VER_CD}
AND		AELM_CD = #{AELM_CD}
AND		PCFM_CD = #{PCFM_CD}
AND		COMPANY_CD = #{COMPANY_CD}

DELETE
FROM HR_JOBDIVBASE_DTL


		AND ACLF_NO = #{ACLF_NO}


		AND VER_CD = #{VER_CD}


		AND AELM_CD = #{AELM_CD}


		AND COMPANY_CD = #{COMPANY_CD}

mapper.update:
