SET	COMPANY_CD = #{COMPANY_CD},
	ACLF_NO = #{ACLF_NO},
	VER_CD = #{VER_CD},
	AELM_CD = #{AELM_CD},
	PCFM_CD = #{PCFM_CD},
	NEED_RQISIT_NM = #{NEED_RQISIT_NM1},
	UPDATE_ID = #{USER_ID},
	UPDATE_IP = #{IP},
	UPDATE_DTS = SYSDATE
WHERE COMPANY_CD = #{COMPANY_CD}
AND ACLF_NO = #{ACLF_NO}
AND VER_CD = #{VER_CD}
AND AELM_CD = #{AELM_CD}
AND PCFM_CD = #{PCFM_CD}
