	ACLF_NO = #{ACLF_NO},
	VER_CD = #{VER_CD},
	AELM_CD = #{AELM_CD},
	PCFM_CD = #{PCFM_CD},
	NEED_RQISIT_NM = #{NEED_RQISIT_NM2},
	UPDATE_ID = #{USER_ID},
	UPDATE_IP = #{IP},
	UPDATE_DTS = SYSDATE
WHERE COMPANY_CD = #{COMPANY_CD}
AND ACLF_NO = #{ACLF_NO}
AND VER_CD = #{VER_CD}
AND AELM_CD = #{AELM_CD}
AND PCFM_CD = #{PCFM_CD}
AND NEED_RQISIT_CD = #{NEED_RQISIT_CD}
AND NEED_RQISIT_FG = 2

UPDATE HR_JOBDIVBASE_SDTL 
SET	COMPANY_CD = #{COMPANY_CD},
	ACLF_NO = #{ACLF_NO},
	VER_CD = #{VER_CD},
	AELM_CD = #{AELM_CD},
	PCFM_CD = #{PCFM_CD},
	NEED_RQISIT_NM = #{NEED_RQISIT_NM3},
	UPDATE_ID = #{USER_ID},
	UPDATE_IP = #{IP},
	UPDATE_DTS = SYSDATE
WHERE COMPANY_CD = #{COMPANY_CD}
AND ACLF_NO = #{ACLF_NO}
AND VER_CD = #{VER_CD}
AND AELM_CD = #{AELM_CD}
AND PCFM_CD = #{PCFM_CD}
AND NEED_RQISIT_CD = #{NEED_RQISIT_CD}
AND NEED_RQISIT_FG = 3

mapper.delete:
DELETE
FROM HR_JOBDIVBASE_SDTL hjs
WHERE COMPANY_CD = #{COMPANY_CD}
AND ACLF_NO = #{ACLF_NO}
AND VER_CD = #{VER_CD}
AND AELM_CD = #{AELM_CD}
